mapper.select:
SELECT pm_company.id AS 'company_id', COUNT(pm_community_resident.id) AS 'person_count' FROM pm_community_resident LEFT JOIN pm_company ON pm_community_resident.company_id = pm_company.id
#{companyId}
GROUP BY pm_company.id

SELECT pm_community_resident.id AS 'community_resident_id', pm_community_resident.name AS 'community_resident_name', pm_community_resident.address, pm_subcontractor.name AS 'subcontractor_name', pm_community_resident.company_id, pm_company.name AS 'company_name', pm_company.parent_id AS 'company_parent_id', pm_phone_number.phone_number FROM pm_community_resident LEFT JOIN pm_company ON pm_community_resident.company_id = pm_company.id LEFT JOIN pm_subcontractor ON pm_community_resident.subcontractor_id = pm_subcontractor.id LEFT JOIN pm_community_resident_phone_number ON pm_community_resident.id = pm_community_resident_phone_number.community_resident_id LEFT JOIN pm_phone_number ON pm_community_resident_phone_number.phone_number_id = pm_phone_number.id
#{companyId}
ORDER BY pm_community_resident.company_id, CONVERT(pm_community_resident.name USING GBK)

SELECT pm_community_resident.id AS 'community_resident_id', pm_community_resident.name AS 'community_resident_name', pm_community_resident.company_id, pm_company.name AS 'company_name' FROM pm_community_resident LEFT JOIN pm_company ON pm_community_resident.company_id = pm_company.id LEFT JOIN pm_community_resident_phone_number ON pm_community_resident.id = pm_community_resident_phone_number.community_resident_id
pm_community_resident_phone_number.phone_number_id IN ( SELECT id FROM pm_phone_number
#{phoneNumber.phoneNumber}
)
AND pm_community_resident.id != #{id}
