mapper.select:
SELECT pm_company.id AS 'company_id', COUNT(pm_community_resident.id) AS 'person_count' FROM pm_community_resident LEFT JOIN pm_company ON pm_community_resident.company_id = pm_company.id
pm_community_resident.company_id = #{companyId}
GROUP BY pm_company.id

SELECT pm_community_resident.id AS 'community_resident_id', pm_community_resident.name AS 'community_resident_name', pm_community_resident.address, pm_system_user.username, pm_community_resident.company_id, pm_company.name AS 'company_name', pm_company.parent_id AS 'company_parent_id', pm_phone_number.phone_number FROM pm_community_resident LEFT JOIN pm_company ON pm_community_resident.company_id = pm_company.id LEFT JOIN pm_system_user ON pm_community_resident.system_user_id = pm_system_user.id LEFT JOIN pm_community_resident_phone_number ON pm_community_resident.id = pm_community_resident_phone_number.community_resident_id LEFT JOIN pm_phone_number ON pm_community_resident_phone_number.phone_number_id = pm_phone_number.id
pm_community_resident.company_id = #{id}
ORDER BY pm_community_resident.company_id, CONVERT(pm_community_resident.name USING GBK)
