mapper.select:
SELECT pm_dormitory_manager.id AS 'dormitory_manager_id', pm_dormitory_manager.name AS 'dormitory_manager_name', pm_dormitory_manager.id_number, pm_dormitory_manager.gender, TIMESTAMPDIFF(YEAR, pm_dormitory_manager.birth, CURDATE()) AS 'age', pm_dormitory_manager.address, pm_phone_number.phone_number, pm_dormitory_manager.create_time AS 'dormitory_manager_create_time', pm_dormitory_manager.update_time AS 'dormitory_manager_update_time', pm_dormitory_manager.company_id, pm_company.name AS 'company_name', pm_system_user.username FROM pm_dormitory_manager LEFT JOIN pm_company ON pm_dormitory_manager.company_id = pm_company.id LEFT JOIN pm_dormitory_manager_phone_number ON pm_dormitory_manager.id = pm_dormitory_manager_phone_number.dormitory_manager_id LEFT JOIN pm_phone_number ON pm_dormitory_manager_phone_number.phone_number_id = pm_phone_number.id LEFT JOIN pm_system_user ON pm_dormitory_manager.system_user_id = pm_system_user.id
pm_dormitory_manager.company_id = #{company.id}
pm_phone_number.phone_number LIKE CONCAT('%', #{value}, '%')
pm_dormitory_manager.id_number = #{value} pm_dormitory_manager.gender = #{value}
pm_dormitory_manager.birth BETWEEN #{v}
pm_system_user.id = #{v}
pm_company.id = #{v}
pm_dormitory_manager.${key} LIKE CONCAT('%', #{value}, '%')
ORDER BY
CONVERT(pm_company.name USING gbk) COLLATE gbk_chinese_ci
CONVERT(dormitory_manager_name USING gbk) COLLATE gbk_chinese_ci
age
CONVERT(pm_dormitory_manager.${key} USING gbk) COLLATE gbk_chinese_ci
DESC
pm_dormitory_manager.company_id, CONVERT(pm_dormitory_manager.name USING GBK)

SELECT pm_dormitory_manager.id AS 'dormitory_manager_id', pm_dormitory_manager.name AS 'dormitory_manager_name', pm_dormitory_manager.id_number, pm_dormitory_manager.gender, pm_dormitory_manager.birth, pm_dormitory_manager.political_status, pm_dormitory_manager.employment_status, pm_dormitory_manager.education, pm_dormitory_manager.address, pm_dormitory_manager.manager_address, pm_dormitory_manager.manager_count, pm_phone_number.phone_number, pm_dormitory_manager.company_id, pm_company.name AS 'company_name', pm_company.parent_id AS 'company_parent_id', pm_system_user.id AS 'system_user_id', pm_system_user.username FROM pm_dormitory_manager LEFT JOIN pm_company ON pm_dormitory_manager.company_id = pm_company.id LEFT JOIN pm_dormitory_manager_phone_number ON pm_dormitory_manager.id = pm_dormitory_manager_phone_number.dormitory_manager_id LEFT JOIN pm_phone_number ON pm_dormitory_manager_phone_number.phone_number_id = pm_phone_number.id LEFT JOIN pm_system_user ON pm_dormitory_manager.system_user_id = pm_system_user.id
pm_dormitory_manager.company_id = #{id}
ORDER BY pm_dormitory_manager.company_id, CONVERT(pm_dormitory_manager.name USING GBK)

SELECT pm_company.id AS 'company_id', COUNT(pm_dormitory_manager.id) AS 'person_count' FROM pm_dormitory_manager LEFT JOIN pm_company ON pm_dormitory_manager.company_id = pm_company.id
pm_dormitory_manager.company_id IN #{companyId}
GROUP BY pm_company.id
