mapper.select:
SELECT pm_dormitory_manager.id AS 'dormitory_manager_id', pm_dormitory_manager.name AS 'dormitory_manager_name', pm_dormitory_manager.id_number, pm_dormitory_manager.gender, pm_dormitory_manager.birth, pm_dormitory_manager.political_status, pm_dormitory_manager.employment_status, pm_dormitory_manager.education, pm_dormitory_manager.address, pm_dormitory_manager.manager_address, pm_dormitory_manager.manager_count, pm_phone_number.phone_number, pm_dormitory_manager.company_id, pm_company.name AS 'company_name', pm_company.parent_id AS 'company_parent_id', pm_subcontractor.id AS 'subcontractor_id', pm_subcontractor.name AS 'subcontractor_name' FROM pm_dormitory_manager LEFT JOIN pm_company ON pm_dormitory_manager.company_id = pm_company.id LEFT JOIN pm_dormitory_manager_phone_number ON pm_dormitory_manager.id = pm_dormitory_manager_phone_number.dormitory_manager_id LEFT JOIN pm_phone_number ON pm_dormitory_manager_phone_number.phone_number_id = pm_phone_number.id LEFT JOIN pm_subcontractor ON pm_dormitory_manager.subcontractor_id = pm_subcontractor.id
#{id}
ORDER BY pm_dormitory_manager.company_id, CONVERT(pm_dormitory_manager.name USING GBK)

SELECT pm_company.id AS 'company_id', COUNT(pm_dormitory_manager.id) AS 'person_count' FROM pm_dormitory_manager LEFT JOIN pm_company ON pm_dormitory_manager.company_id = pm_company.id
pm_dormitory_manager.company_id IN #{companyId}
GROUP BY pm_company.id
