mapper.select:
SELECT company_id, COUNT(id) AS 'person_count' FROM pm_subcontractor
#{companyId}
GROUP BY company_id

SELECT pm_subcontractor.name AS 'subcontractor_name', COUNT(DISTINCT pm_community_resident.id) AS 'community_resident_count', COUNT(DISTINCT pm_dormitory_manager.id) AS 'dormitory_manager_count' FROM pm_subcontractor LEFT JOIN pm_dormitory_manager ON pm_subcontractor.id = pm_dormitory_manager.subcontractor_id LEFT JOIN pm_community_resident ON pm_subcontractor.id = pm_community_resident.subcontractor_id
#{companyId}
GROUP BY pm_subcontractor.name
