mapper.select:
SELECT pm_system_permission.id AS 'permission_id', pm_system_permission.name AS 'permission_name', pm_system_permission.function_name, pm_system_permission.uri, pm_system_permission.http_methods, pm_system_permission.parent_id AS 'permission_parent_id', pm_system_permission.order_by, pm_system_permission.menu_type, pm_system_permission.is_display, pm_system_permission.create_time AS 'permission_create_time', pm_system_permission.update_time AS 'permission_update_time' FROM pm_system_permission
LEFT JOIN pm_company_permission ON pm_system_permission.id = pm_company_permission.permission_id
pm_system_permission.menu_type IN (0, 1) AND pm_system_permission.is_display = #{isDisplay}
AND pm_system_permission.parent_id = 0
#{companyId}
